			$('.addInFullAmountPaymentField').on('change', function() {

                $(".divShowForOnChangePaymentType").hide();
                if ( this.value == 'Wire'){
                    $("#showWire").show();
                    
                }else if ( this.value == 'MG'){
                    $("#showMG").show();
                }else{
                    $("#showWU").show();
                }
			});
		});
</script>
